package Tema_3;

public class Ejercicio_For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Incremento del 0 al 9
		for (int i=0; i<10; i++) {
			System.out.println(i);

		}
		//aumento del 1 al 10
		for (int i=1; i <= 10; i++) {
			System.out.println(i);

		}
		//disminuye del 10 al 0 
		for (int i=10; i >= 0; i--) {
			System.out.println(i);

		}
	}

}
